Node.Signals.InitCanvas = function () {
    let canvas = document.createElement("canvas");
    let ctx = Node.Inputs.group.getDOMElement().appendChild(canvas);

    const array_label = [];
    const array_data = [];
    for (let i = 0; i < Node.Inputs.datas.items.length; i++) {
        array_label.push(Node.Inputs.datas.items[i].data.labels);
        array_data.push(Node.Inputs.datas.items[i].data.data);
    };

    myChart = new Chart(ctx, {
        type: 'bar',
        data: {
            maintainAspectRatio: false,
            labels:array_label,
            datasets: [{
                label: '# of Votes',
                data: array_data,
                backgroundColor: [
                    'rgba(255, 99, 132, 0.2)',
                    'rgba(54, 162, 235, 0.2)',
                    'rgba(255, 206, 86, 0.2)',
                    'rgba(75, 192, 192, 0.2)',
                    'rgba(153, 102, 255, 0.2)',
                    'rgba(255, 159, 64, 0.2)'
                ],
                borderColor: [
                    'rgba(255, 99, 132, 1)',
                    'rgba(54, 162, 235, 1)',
                    'rgba(255, 206, 86, 1)',
                    'rgba(75, 192, 192, 1)',
                    'rgba(153, 102, 255, 1)',
                    'rgba(255, 159, 64, 1)'
                ],
                borderWidth: 1
            }]
        },
        options: {
            responsive: true,
            maintainAspectRatio: false,
            scales: {
                y: {
                    beginAtZero: true
                }
            }
        }
    });
}

Node.Signals.Update = function () {
    myChart.data.labels.push("NEW");
    let val = Node.Inputs.value;

    myChart.data.datasets.forEach((dataset) => {
        dataset.data.push(val);
    });
    myChart.update();
}

