Node.Inputs={
    width:'number',
    height:'number',
    group:'reference',
    mode:'boolean',
    penSize:'number',
    penColor:'color',
    text:'string',
    filePath:'*',
}

let canvas;
let canvas_element;
let ctx;
var zoom = 100;
var zoomStep = 10;

function setPosition(e) {
    Node.Outputs.PointerX = e.clientX
    Node.Outputs.PointerY = e.clientY
}

Node.OnInit = function () {
    document.body.addEventListener('mousemove', setPosition)
    document.body.addEventListener('mousedown', setPosition)
}

Node.OnDestroy = function () {
    document.body.removeEventListener('mousedown', setPosition)
    document.body.removeEventListener('mousemove', setPosition)
}

Node.Signals.InitCanvas = function(){
    canvas_element = document.createElement("canvas");
    cnvs=Node.Inputs.group.getDOMElement().appendChild(canvas_element);

    //User Pointer
    let div_element = document.createElement("div");
    let div=Node.Inputs.group.getDOMElement().appendChild(div_element);

    canvas = new fabric.Canvas(cnvs,
    {
        freeDrawingCursor: 'none',//自作のドットカーソルを表示するために必要
        isDrawingMode: true
    });

    //影
    //canvas.freeDrawingBrush.setShadow("2px 2px 10px rgba(0,0,0,0.2)");

    //フリードローイング
    canvas.isDrawingMode = true;

    //描画色の設定。RGB+不透明度。"rgb(255,50,40)"と書くとアルファを省略できる
    canvas.freeDrawingBrush.color = "rgba(255,50,40,0.5)"; 
    //ペンサイズの設定
    canvas.freeDrawingBrush.width = 10;

    //オブジェクト削除
    document.addEventListener("keyup", function (e) {
        if (e.keyCode == 8 |  e.keyCode == 46) {  // delete と backspaceに対応
        canvas.remove(canvas.getActiveObject());
        }
    });
}

Node.Signals.ChangeCanvas = function(){
    //サイズを設定
    canvas.setWidth(Node.Inputs.width);
    canvas.setHeight(Node.Inputs.height);
}

Node.Signals.ChangeMode = function(){
    canvas.isDrawingMode = Node.Inputs.mode;
    canvas.selection=false; //単一選択
    //rect.set('selectable',false); //対象を選択モードにしない
}

Node.Signals.Clear = function(){
    canvas.clear();
}

Node.Signals.ZoomUp = function(){
    if (zoom < 500) {
		zoom += zoomStep;
		canvas.setZoom(zoom / 100);
	};
}

Node.Signals.ZoomDown = function(){
	if (zoom > 10) {
		zoom -= zoomStep;
		canvas.setZoom(zoom / 100);
	};
}


Node.Signals.AddText = function(){
    var text = new fabric.Text(Node.Inputs.text,{
        left:100,
        top:100
    });
    canvas.add(text);
}

Node.Signals.AddRect = function(){
    var rect = new fabric.Rect({
      left: 100,
      top: 100,
      fill: 'red',
      width: 50,
      height: 50
    });
    canvas.add(rect);
}


Node.Signals.AddCircle = function(){
    var circle = new fabric.Circle({
        radius: 50, fill: 'green', left: 100, top: 100
    });
    canvas.add(circle);
}

Node.Signals.AddEllipse = function(){
    var ellipse = new fabric.Ellipse({
        left: 50,         //左上角相当部分（赤点）の左
        top: 70,          //左上角相当部分（赤点）の上
        rx: 50,           //水平半径
        ry: 30,           //垂直半径
        strokeWidth: 3,   //線の太さ
        stroke: 'blue',   //線の色
        fill: 'lightblue',//塗潰しの色
        angle: 0          //角度
    });
    // "add" rectangle onto canvas
    canvas.add(ellipse);
}

Node.Signals.AddLine = function(){
    var line = new fabric.Line([40,70,160,120],//始点x,y,終点x,y
        {strokeWidth: 3,stroke: 'blue'});
    canvas.add(line);
}

Node.Signals.AddPolyline = function(){
    var polyline = new fabric.Polyline([
      {x: 40, y: 60},       //始点
      {x: 160, y: 120},     //中間点1
      {x: 60, y: 110},      //中間点2
      {x: 140, y: 80}],
      {    //終点
        strokeWidth: 3,       //線の太さ
        stroke: 'blue',       //線の色
        fill: 'lightblue',    //塗潰しの色
        angle: 0              //角度
    　});
    canvas.add(polyline)
}

Node.Signals.AddTriangle = function(){
    var triangle = new fabric.Triangle({
        width: 50, height: 60, fill: 'blue', left: 50, top: 50
    });
    canvas.add(triangle);
}



Node.Signals.AddBackgroundImage = function(){
    var fileData = Node.Inputs.filePath;
    console.log(fileData);
    // 画像ファイル以外は処理を止める
    if(!fileData.type.match('image.*')) {
        alert('画像を選択してください');
        return;
    }
    // FileReaderオブジェクトを使ってファイル読み込み
    var reader = new FileReader();
    //canvas.setBackgroundImage(fileData, canvas.renderAll.bind(canvas));
    // ファイル読み込みに成功したときの処理
    reader.onload = function() {
        canvas.setBackgroundImage(reader.result, function(bkImg){
            bkImg.scaleToWidth(canvas.width);
            bkImg.scaleToHeight(canvas.height);
            canvas.requestRenderAll();
        },{
            opacity:0.3,
            //left: (canvas.width- bkImg.width * bkImg.scaleX ) / 2
        });
    }
    // ファイル読み込みを実行
    reader.readAsDataURL(fileData);
}

Node.Signals.AddImage = function(){
    var fileData = Node.Inputs.filePath;
    const img_path = fileData;
    fabric.Image.fromURL(img_path,(img) => {
    canvas.setDimensions({
      width: img.width,
      height: img.height
    });
    canvas.setBackgroundImage(img,() => {
        canvas.requestRenderAll();
        });
    })
}

Node.Signals.SaveData = function(){
  //let canvasToDL = document.getElementById("canvas");
  let link = document.createElement("a");
  link.href = cnvs.toDataURL("image/png");
  link.download = "drawing.png";
  link.click();
}


Node.Setters.penSize = function (value) {
    //ペンサイズの設定
    canvas.freeDrawingBrush.width = value; // 描画する線の太さ
}

Node.Signals.PenColor = function () {
    //ペンサイズの設定
    canvas.freeDrawingBrush.color = Node.Inputs.penColor; // 描画する線の太さ
    console.log("pen");
}

