    task_click:'signal',
}

let gantt;
let taskList;
let reader = new FileReader();

Node.Signals.InitCanvas = function(){
    taskList=[];
    let canvas = document.createElement("div");
    let ctx=Node.Inputs.group.getDOMElement().appendChild(canvas);

    var names = [
        ["Redesign website", [0, 7]],
        ["Write new content", [1, 4]],
        ["Apply new styles", [3, 6]],
        ["Review", [7, 7]],
        ["Deploy", [8, 9]],
        ["Go Live!", [10, 10]]
    ];

    taskList = names.map(function(name, i) {
        var today = new Date();
        var start = new Date(today.getFullYear(), today.getMonth(), today.getDate());
        var end = new Date(today.getFullYear(), today.getMonth(), today.getDate());
        start.setDate(today.getDate() + name[1][0]);
        end.setDate(today.getDate() + name[1][1]);
        return {
            start: start,
            end: end,
            name: name[0],
            id: "Task " + i,
            progress: parseInt(Math.random() * 100, 10)
        }
    });
    taskList[1].progress = 0;
    taskList[1].dependencies = "Task 0";
    taskList[2].dependencies = "Task 1";
    taskList[3].dependencies = "Task 2";
    taskList[5].dependencies = "Task 4";
    //taskList[5].custom_class = "bar-milestone";

	gantt = new Gantt(ctx, taskList, {
	    header_height: 50,
        view_modes: ['Quarter Day', 'Half Day', 'Day', 'Week', 'Month'],
        bar_height: 30,
        bar_corner_radius: 3,
        arrow_curve: 5,
        padding: 14,
        view_mode: 'Day',
        date_format: 'YYYY-MM-DD',
		language: 'jp',
        custom_popup_html: null,

		on_click: function (task) {
			//console.log(task);
            Node.Outputs.task_n=task.name;
            Node.Outputs.task_i=task.id;
            Node.Outputs.task_s=task.start;
            Node.Outputs.task_e=task.end;
            Node.Outputs.task_d=task.dependencies;
            Node.Outputs.task_p=task.progress;
			Node.Outputs.task_click();
		},
		on_date_change: function(task, start, end) {
			//console.log(task, start, end);
			for (var t in taskList){
    			if(taskList[t].id==task.id){
    		    	taskList[t].start=start;
    			    taskList[t].end=end;
    	    		break;
			    }
			};
		},
		on_progress_change: function(task, progress) {
			//console.log(task, progress);
			for (var t in taskList){
    			if(taskList[t].id==task.id){
    		    	taskList[t].progress=progress;
    	    		break;
			    }
			};
		},
		on_view_change: function(mode) {
			//console.log(mode);
		},
	});
}

Node.Signals.Change_view = function(){
    gantt.change_view_mode(Node.Inputs.view_mode);
    // Quarter Day, Half Day, Day, Week, Month 
}



Node.Signals.Change_options = function(){
    var options = Noodl.Object.get('Options');
    gantt.options.header_height= Number(options.data.header_height);
    gantt.options.bar_height= Number(options.data.bar_height);
    gantt.options.padding= Number(options.data.padding);
	gantt.options.language= options.data.language;
    gantt.refresh(taskList);
}

Node.Signals.Change_task = function(){
    var task = Noodl.Object.get('Task');
    for (var t in taskList){
    	if(taskList[t].id==task.data.task_id){
        	taskList[t].name=task.data.task_name;
        	taskList[t].progress = task.data.task_progress;
        	taskList[t].dependencies = task.data.task_dependencies;
        	taskList[t].start=task.data.task_start;
    	    taskList[t].end=task.data.task_end;
    		break;
	    }
	};
    gantt.refresh(taskList);
    //console.log(gantt);
}

Node.Signals.Add_task = function(){
    var task = CreateTask();
    taskList.push(task);
    gantt.refresh(taskList);

    function CreateTask() {
        var start = new Date();
        start.setDate(start.getDate());
        var end = new Date();
        end.setDate(start.getDate() + parseInt(Node.Inputs.task_term,10));
        return {
            start: start,
            end: end,
            name: Node.Inputs.task_name,
            id: 'Task ' + taskList.length,
            progress: 0,
            dependencies: Node.Inputs.task_depend
        };
    }
}

Node.Signals.Delete_task = function(){
    var result = taskList.filter(function( task ) {
        return task.id !== Node.Inputs.task_id;
    });
    taskList=result;
    gantt.refresh(taskList);
}

Node.Signals.FileLoad = function () {
    LoadArray = [];                  // 初期化.  
    taskList = [];                  // 初期化.  
    //console.log(Node.Inputs.file);
    reader.readAsText(Node.Inputs.file, 'UTF-8');
    reader.onload = () =>{
        console.log("LoadData");
        LoadArray=JSON.parse(reader.result);//JSONテキストを配列にする
        console.log("LoadArray");
        console.log(LoadArray);

        taskList = LoadArray.map(function(name, i) {
            var start = new Date(LoadArray[i].start);
            var end = new Date(LoadArray[i].end);
            return {
                start: start,
                end: end,
                name: LoadArray[i].name,
                id: LoadArray[i].id,
                progress: LoadArray[i].progress,
                dependencies: LoadArray[i].dependencies            
            }
        });
        gantt.refresh(taskList);        
    }
}

Node.Signals.FileSave = function () {
    //console.log(taskList);
    let taskString = JSON.stringify(taskList);
    //console.log(taskString);
    let blob = new Blob([taskString],{type:"text/plan"});
    let link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = 'gantt_data.json';
    link.click();
}


